mapper.select:
SELECT count(*) FROM m_menu WHERE del_flag=0
AND menu_id like CONCAT('%',#{menuId,jdbcType=VARCHAR},'%' )
AND menu_name like CONCAT('%',#{menuName,jdbcType=VARCHAR},'%' )
AND menu_url like CONCAT('%',#{menuUrl,jdbcType=VARCHAR},'%' )
AND menu_level = #{menuLevel,jdbcType=VARCHAR}
AND menu_pid = #{menuPid,jdbcType=VARCHAR}

SELECT id, menu_id, menu_name, menu_server, menu_url, CASE WHEN menu_level = '0' THEN '一级菜单' ELSE '二级菜单' END AS menu_level, menu_pid, menu_sort, menu_icon, (SELECT menu_name from m_menu where menu_id = m.menu_pid) AS menuParentName FROM m_menu m WHERE del_flag='0'
AND menu_id like CONCAT('%',#{menuId,jdbcType=VARCHAR},'%' )
AND menu_name like CONCAT('%',#{menuName,jdbcType=VARCHAR},'%' )
AND menu_url like CONCAT('%',#{menuUrl,jdbcType=VARCHAR},'%' )
AND menu_level = #{menuLevel,jdbcType=VARCHAR}
AND menu_pid = #{menuPid,jdbcType=VARCHAR}
order by menu_id
limit #{start},#{rows}
